class App extends React.Component{
        constructor(){
                super()
                this.state = {
                        todos: todoDatas
                }
                this.handleChange = this.handleChange.bind(this)
        }

        handleChange(id){
                alert(id)
        }

        render() {

                const todoData = this.state.todos.map(item => {
                        return (
                            <ToDoItem key={item.id} item={item}/>
                        )
                })

                return (
                    <div className="todo-list">
                            {todoData}
                    </div>
                )
        }
}



export default App
