        super()
        this.state = {
            loading: false,
            data: {}
        }
    }

    componentDidMount() {
        this.setState({
            loading: true
        })

        fetch("https://swapi.dev/api/people")
            .then(response => response.json())
            .then(data => console.log(data)
                // this.setState({
