import React from "react"

import ApiData from "./component/apiData"

class  App extends React.Component{
    constructor(){
        super()
        this.state = {
            loading: false,
            data: {},
            apiData: ''
        }
    }

    componentDidMount() {
        this.setState({
            loading: true
        })

        fetch("https://swapi.dev/api/people")
            .then(response => response.json())
            .then(data => {
                this.setState({
                    data: data.results,
                    loading: false
                })
            })
    }

    apiData = this.state.data.map( item => {
        return(
            <ApiData key={item.name} name={item.name} />
        )
    })

    render() {
        // const text = this.state.loading ? 'loading...' : this.state.data
        return(
            <div>
                {this.state.apiData}
            </div>
        )
    }
}

export default App
