import React from "react"

class  App extends React.Component{
    constructor(){
        super()
        this.state = {
            loading: false,
            character: {}
        }
    }

    componentDidMount() {
        this.setState({
            loading: true
        })

        fetch("https://swapi.dev/api/people/1")
            .then(response => response.json())
            .then(data => {
                this.setState({
                    character: data
                })
                this.setState({
                    loading: false
                })
            })
    }

    render() {
        const text = this.state.loading ? 'loading...' : this.state.character.name
        return(
            <div>
                {text}
            </div>
        )
    }
}

export default App
