import React from "react"

class Form extends React.Component{
    constructor(){
        super()
        this.state = {
            firstName: "",
            lastName: "",
            age: 0,
            gender: "male",
            destination: "",
            isVegan: false,
            isKosher: false,
            isLactoseFree: false,

        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event){
        const {name, value, type, checked} = event.target
        type === "checkbox" ? this.setState({
            [name]: checked
        }) : this.setState({
            [name]: value
        })
    }

    render() {

    }
}

export default Form
